-- Table structure for tb_album
-- ----------------------------
DROP TABLE IF EXISTS `tb_album`;
CREATE TABLE `tb_album` (
  `ALBUM_ID` int(11) NOT NULL AUTO_INCREMENT COMMENT '相册标识\r\n            自增',
  `USER_NAME` varchar(20) DEFAULT NULL,
  `ALBUM_NAME` varchar(64) DEFAULT NULL COMMENT '相册名称',
  `ALBUM_NUM` int(11) DEFAULT NULL COMMENT '相册内相片数量',
  `ALBUM_MEMO` varchar(512) DEFAULT NULL COMMENT '相册描述',
  `ALBUM_LEVEL` int(11) DEFAULT NULL COMMENT '相册访问级别',
  `ALBUM_CREATE_TIME` datetime DEFAULT NULL COMMENT '相册创建时间',
  `ALBUM_LAST_MODIFY_TIME` datetime DEFAULT NULL COMMENT '相册最后修改时间',
  `ALBUM_PHOTO_ID` bigint(20) DEFAULT NULL COMMENT '相册封面相片标识',
  PRIMARY KEY (`ALBUM_ID`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='相册\r\n其它说明';

-- ----------------------------
-- Records of tb_album
-- ----------------------------

