-- ----------------------------
DROP TABLE IF EXISTS `tb_photo`;
CREATE TABLE `tb_photo` (
  `PHOTO_ID` bigint(20) NOT NULL COMMENT '相片标识',
  `ALBUM_ID` int(11) DEFAULT NULL COMMENT '相册标识\r\n            自增',
  `PHOTO_NAME` varchar(128) DEFAULT NULL COMMENT '相片名称',
  `PHOTO_SIZE` bigint(20) DEFAULT NULL COMMENT '相片大小',
  `PHOTO_UPLOAD_TIME` datetime DEFAULT NULL COMMENT '相片上传时间',
  `PHOTO_PATH` varchar(64) DEFAULT NULL COMMENT '相片存放位置',
  `PHOTO_MEMO` varchar(512) DEFAULT NULL COMMENT '相片备注',
  PRIMARY KEY (`PHOTO_ID`),
  KEY `FK_R_ALBUM_PHOTO_1` (`ALBUM_ID`),
  CONSTRAINT `FK_R_ALBUM_PHOTO_1` FOREIGN KEY (`ALBUM_ID`) REFERENCES `tb_album` (`ALBUM_ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='相片';

